import React from 'react'
import { CATEGORIES } from './data'

export default class Categories extends React.Component {

  onClick = (e) => {
    this.props.changeCategory(e.target.innerText)
  }

  renderCategories = () => {
    console.log(this.props.currentCategory)
    return CATEGORIES.map(cat => {
      if(this.props.currentCategory === cat.category){
       return  <button className="selected" onClick={this.onClick}>{cat}</button>
      }else{
        return <button className="none" onClick={this.onClick}>{cat}</button>
      }
    })

  }

  render() {
    return(
      <div className="categories">
        {this.renderCategories()}
      </div>
    )
  }
}
