import React from 'react';
import './App.css';
import Categories from './Categories'
import Tasks from './Tasks'

class App extends React.Component {

  state = {
    currentCategory: 'All',
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  changeCategory = (cat) => {
    this.setState({currentCategory: cat})
  }

  renderTasks = () => {
    if(this.state.currentCategory === 'All'){
      return this.state.tasks
    }
    return this.state.tasks.filter(task => task.category === '')
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <Categories changeCategory={this.changeCategory}/>
        <div className="tasks">
          <h5>Tasks</h5>
          <Tasks tasks={this.state.tasks}/>
        </div>
      </div>
    );
  }
}


export default App;
